sources.style.scss:
@import "/base.scss";
@import "/utilities.scss";

html {
    height: 100%;
}

body {
    min-height: 100%;
    background-color: #4F73CD;
}

.container{
    display: flex;
    background-color: #4F73CD;
    height: 100%;
}

.card-container {
    display: flex;
    cursor: pointer;
}

.swiper-wrapper {
    height: auto !important;
} 

.swiper-button-next, .swiper-button-prev {
    display: none !important;
}

.container__content {
    display: flex;
    width: 95%;
    height: 90%;
    margin: 36px 10px;
    flex-direction: column;
    align-items: center;
}

.title {
    font-family: "Golos";
    font-size: 30px;
    font-weight: 600;
    color: #ffffff;
    margin-bottom: 20px;
}

.dropdown {
    display: flex;
    flex-direction: column;
    width: 334px;
    height: auto;
    background-color: transparent;
    position: relative;
    color: #ffffff;
}

.dropdown__name {
    display: flex;
    align-items: center;
    font-family: "Golos";
    font-weight: 500;
    font-size: 16px;
    height: 39px;
    position: relative;
    cursor: pointer;
}

.dropdown__name::after {
    content: '';
	top: 50%;
	right: 15px;
}

.dropdown__list {
    display: none;
	width: 100%;
	height: auto;
	left: 0px;
	border-top: none;
	background: #254595;
    user-select: none;
    border-radius: 0 0 10px 10px;
    margin-bottom: 30px;
}

.arrow-icon {
    display: block;
    right: 0;
    position: absolute;
    margin: 10px;
}

.arrow-icon_clicked {
    display: block;
}

.dropdown_open .dropdown__list {
    display: block;
    transition: all 0.6s ease 0s;
}

.dropdown_open .arrow-icon {
    transform: rotate(180deg);
}

.dropdown__item {
    display: flex;
    list-style: none;
    font-family: "Golos";
    font-weight: 500;
    font-size: 16px;
    height: 39px;
    color: #ffffff;
    align-items: center;
    padding-left: 41px;
    cursor: pointer;
}

.dropdown__item_clicked {
    background-color: #3259B9;
}

.dropdown__item_clicked .tick {
    display: flex;
}

.tick {
    position: absolute;
    width: 25px;
    height: 100%;
    margin: 0 8px 0 8px;
    left: 0;
    display: none;
}


.full-card {
    display: flex;
    box-sizing: border-box;
    flex-direction: column;
    height: 482px;
    width: 334px;
    position: absolute;
    top: 0;
    z-index: 4;
    left: 0;
}

.full-card__picture {
    display: flex;
    width: 340px;
    height: 241px;
    border-radius: 10px 10px 0 0;
    object-fit: cover;
}

.card-about {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    width: 340px;
    height: 241px;
    border-radius: 0 0 10px 10px;
    background-color: #254595;
    padding: 30px 20px 20px 20px;
    position: relative;
}

.icon {
    display: flex;
    background-color: transparent;
    border: 0;
    position: absolute;
    right: 20px;
    top: 20px;
    justify-content: center;
    align-items: center;
}

.icon__img {
    width: 15px;
    height: 15px;
}

.full-card__text {
    font-family: "Golos";
    font-weight: 400;
    font-size: 16px;
    color: #ffffff;
}

.card-about__button {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 50px;
    width: 132px;
    border-radius: 8px;
    font-family: "Golos";
    font-weight: 400;
    font-size: 16px;
    color: #ffffff;
    border: 2px solid #ffffff;
    background-color: transparent;
    margin-top: 20px;
    text-decoration: none;
}

.card-about__button:hover {
    background-color: #0047FE;
    border: none;
}

.container__cards-flex {
    display: flex;
    width: 352px;
    flex-wrap: wrap;
    // justify-content: center;
    position: relative;
}

.swiper-container {
    width: 345px;
    margin-left: auto;
    margin-right: auto;
    height: 485px;
}
  
 .swiper-slide{
     position: initial !important;
 } 

.card {
    width: 158px;
    height: 230px;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    padding-left: 10px;
    padding-bottom: 10px;
    border-radius: 10px;
    background-size: cover;
    position: relative;
}

.image {
    width: 158px;
    height: 230px;
    object-fit: cover;
    position: absolute;
    bottom: 0;
    left: 0;
    border-radius: 10px;
}

.card__country {
    font-family: "Golos";
    font-family: 400;
    font-size: 16px;
    color:#ffffff;
    z-index: 3;
}

.city {
    font-family: "Golos";
    font-weight: 600;
}

.disposition_absolute {
    font-family: "Golos";
    font-family: 400;
    font-size: 16px;
    color:#ffffff;
    z-index: 3;
    left: 12px;
    bottom: 258px;
    position: absolute;
}

.current__city {
    font-family: "Golos";
    font-weight: 600;
    bottom: 279px;
}

.dropdown__item:hover {
    background-color: #3259B9;
}

.pagination {
    position: static !important;
}

.swiper-pagination-bullet {
    margin-left: 10px;
    background-color: #ffffff !important;
}

@media (min-width: 768px) {
    
    .title {
        font-size: 36px;
    }
    
    .full-card {
        width: 700px;
        height: 317px;
        flex-direction: row;
        top: auto;
    }
    .full-card__picture {
        width: 216px;
        height: 317px;
        border-radius: 10px 0 0 10px;
        
    }
    
    .card-about {
        border-radius: 0 10px 10px 0;
        width: 480px;
        height: 317px;
    }
    
    .card-about__button {
        margin-top: 94px;
    }
    
    .swiper-container {
        width: 702px;
        height: 670px;
    }
    
    .card {
        width: 216px;
        height: 317px;
        background-size: cover;
    }
    
    .image {
        width: 216px;
        height: 317px;
    }
    
    .card__country {
        font-family: "Golos";
        font-family: 400;
        font-size: 16px;
        color:#ffffff;
    }
    .city {
        font-family: "Golos";
        font-weight: 600;
    }
    
    .disposition_absolute {
        bottom: 17px;
    }
    
    .current__city {
        bottom: 38px;
    }
    
    .dropdown {
        display: flex;
        width: 689px;
    }
    
    .dropdown__name {
        display: none;
    }
    
    .dropdown__list {
        display: flex;
        flex-wrap: wrap;
        border-radius: 0;
        background-color: transparent;
    }
    
    .arrow-icon, .arrow-icon_clicked {
        display: none;
    }
    
    .dropdown__item {
        justify-content: center;
        padding: 0;
        width: 131px;
        border-radius: 10px;
    }
    
    .dropdown__item:hover {
        background-color: #80A0F2;
    }
    
    .dropdown__item_clicked {
        background: #80A0F2;
    }
    
    .dropdown__item_clicked .tick {
        display: none;
    }
    
    .container__content {
        margin: 36px 20px;
    }
    
    .swiper-button-next, .swiper-button-prev {
        display: none;
    }
    
    
}

@media (min-width: 1440px) {
    
    .container__content {
        width: 100%;
        margin: 36px 20px;
    }
    
    .title {
        font-size: 36px;
    }
    
    .full-card {
        width: 570px;
        height: 250px;
    }
    
    .full-card__picture {
        border-radius: 10px 0 0 10px;
        height: 250px;
        width: 170px;
        object-fit: cover;
        object-position: 0;
    }
    
    .card-about {
        border-radius: 0 10px 10px 0;
        width: 400px;
        height: 250px;
        padding: 30px 20px 20px 40px;
    }
    
    .border_left {
        border-radius: 10px 0 0 10px;
    }
    .card-about__button {
        margin-top: 14px;
        
    }
    
    .reverse_side {
        border-radius: 0 10px 10px 0;
    }
    
    .swiper-container {
        width: 1170px  !important;
        height: 550px;
        margin-left: auto;
        margin-right: auto;
    }

    .card {
        background-size: cover;
        width: 170px;
        height: 250px;
    }
    
    .image {
        width: 170px;
        height: 250px;
    }
    
    .card__country {
        font-family: "Golos";
        font-family: 400;
        font-size: 16px;
        color:#ffffff;
    }
    .city {
        font-family: "Golos";
        font-weight: 600;
    }
    
    .card-container {
        position: relative;
    }
    
    .full-card_order_reverse {
        right: 0;
        left: auto !important;
        flex-direction: row-reverse;
    }
    
    .full-card_order_reverse .card-about {
        border-radius: 10px 0 0 10px;
    }
    
    .full-card_order_reverse .full-card__picture {
        border-radius: 0 10px 10px 0;
    }
    
    .full-card_order_reverse .icon {
        right: auto;
        left: 20px;
    }
    
    .full-card_order_reverse .disposition_absolute {
        left: 412px
    }
    
    .dropdown {
        width: 1170px;
    }
    
    .dropdown__list {
        width: 100%;
    }
    
    .dropdown__item:hover {
        background-color: #80A0F2;
    }
    
    .swiper-button-next, .swiper-button-prev {
        display: flex !important;
        background-color: yellow;
        cursor: pointer !important;
        top: 57% !important;
    }
    
    .pagination {
        display: none !important;
    }
}
